xquery version "1.0" encoding "UTF-8";
let $doc as document-node() := document { <Root> <!-- Comment 1 --> <Child/> <!-- Comment 2 --> </Root> }
let $xsl as document-node() := document { <xsl:stylesheet version="2.0" xmlns:xsl="http://www.w3.org/1999/XSL/Transform" xmlns:xs="http://www.w3.org/2001/XMLSchema" xmlns:fn="http://www.w3.org/2005/xpath-functions" exclude-result-prefixes="#all"> <xsl:template match="/"> <CommentsInXslt> <xsl:value-of select="count(.//comment())"/> </CommentsInXslt> </xsl:template> </xsl:stylesheet> }
return <Results> <CommentsInXQuery>{ count($doc//comment()) }</CommentsInXQuery> { transform:transform($doc, $xsl, ()) } </Results>

