try {
    fn:error(QName('http://foobar', 'foo:BAR'), "foofoo", <barbar/>)
}
catch * {
    $err:code,'-', exists($err:value), $err:description,
    $err:module, '(', $err:line-number, ',', $err:column-number, ')'
}
